import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import anime from 'animejs/lib/anime.es.js';
import '../styles/Hero.css';

const Hero = () => {
    const heroRef = useRef(null);
    const textRef = useRef(null);
    const imageRef = useRef(null);
    const shapesRef = useRef(null);

    useEffect(() => {
        // Make sure the DOM elements are available
        if (!heroRef.current || !textRef.current || !imageRef.current || !shapesRef.current) return;

        // Timeline for coordinated animations
        const timeline = anime.timeline({
            easing: 'easeOutExpo',
            duration: 1000
        });

        // Animate shapes in background
        timeline.add({
            targets: '.hero-shape',
            translateY: [100, 0],
            translateX: [100, 0],
            opacity: [0, 1],
            delay: anime.stagger(100),
            easing: 'easeOutQuad',
        }, 0);

        // Animate hero image
        timeline.add({
            targets: imageRef.current,
            translateX: [100, 0],
            opacity: [0, 1],
            easing: 'easeOutQuad'
        }, 200);

        // Animate heading
        timeline.add({
            targets: textRef.current.querySelector('h1'),
            translateY: [40, 0],
            opacity: [0, 1],
            easing: 'easeOutQuad'
        }, 400);

        // Animate paragraph
        timeline.add({
            targets: textRef.current.querySelector('p'),
            translateY: [20, 0],
            opacity: [0, 1],
            easing: 'easeOutQuad'
        }, 600);

        // Animate CTA buttons
        timeline.add({
            targets: textRef.current.querySelectorAll('.btn'),
            translateY: [20, 0],
            opacity: [0, 1],
            delay: anime.stagger(100),
            easing: 'easeOutQuad'
        }, 800);

        // Animate feature stats
        timeline.add({
            targets: '.hero-stats .stat-item',
            translateY: [20, 0],
            opacity: [0, 1],
            delay: anime.stagger(100),
            easing: 'easeOutQuad'
        }, 900);

        // Floating animation for decorative shapes (continuous)
        anime({
            targets: '.hero-shape',
            translateY: function() { 
                return anime.random(-15, 15); 
            },
            translateX: function() { 
                return anime.random(-15, 15); 
            },
            rotate: function() { 
                return anime.random(-10, 10); 
            },
            duration: 3000,
            easing: 'easeInOutSine',
            delay: anime.stagger(200),
            complete: function(anim) {
                anime({
                    targets: '.hero-shape',
                    translateY: function() { 
                        return anime.random(-15, 15); 
                    },
                    translateX: function() { 
                        return anime.random(-15, 15); 
                    },
                    rotate: function() { 
                        return anime.random(-10, 10); 
                    },
                    duration: 3000,
                    easing: 'easeInOutSine',
                    delay: anime.stagger(200),
                    complete: anim.complete
                });
            }
        });

        // Subtle pulse animation for CTA button (continuous)
        anime({
            targets: '.btn-primary',
            scale: [1, 1.05, 1],
            duration: 2000,
            easing: 'easeInOutSine',
            loop: true
        });
    }, []);

    return (
        <section className="hero-section" ref={heroRef}>
            <div className="hero-background">
                <div className="hero-shapes" ref={shapesRef}>
                    <div className="hero-shape shape-circle"></div>
                    <div className="hero-shape shape-dots"></div>
                    <div className="hero-shape shape-square"></div>
                    <div className="hero-shape shape-triangle"></div>
                    <div className="hero-shape shape-ring"></div>
                </div>
            </div>

            <div className="container">
                <div className="hero-content">
                    <div className="hero-text" ref={textRef}>
                        <h1>Shape Your Future in <span className="highlight">Information Technology</span></h1>
                        <p>Gain cutting-edge skills and knowledge with our industry-focused IT programs at Forum Information Academy. Join a community of innovators shaping the digital future.</p>
                        <div className="hero-cta">
                            <Link to="/courses" className="btn btn-primary">
                                Browse Courses
                                <span className="material-icons">arrow_forward</span>
                            </Link>
                            <Link to="/about" className="btn btn-outline">Learn More</Link>
                        </div>

                        <div className="hero-stats">
                            <div className="stat-item">
                                <span className="stat-number">93%</span>
                                <span className="stat-label">Job Placement</span>
                            </div>
                            <div className="stat-item">
                                <span className="stat-number">50+</span>
                                <span className="stat-label">Industry Partners</span>
                            </div>
                            <div className="stat-item">
                                <span className="stat-number">24/7</span>
                                <span className="stat-label">Learning Access</span>
                            </div>
                        </div>
                    </div>

                    <div className="hero-image" ref={imageRef}>
                        <img src="/images/AI1.png" alt="Students at Forum Information Academy" />
                        <div className="hero-image-overlay"></div>
                        {/* <div className="tech-circles">
                            <span className="tech-circle ai"></span>
                            <span className="tech-circle web"></span>
                            <span className="tech-circle data"></span>
                            <span className="tech-circle cloud"></span>
                            <span className="tech-circle security"></span>
                        </div> */}
                        <div className="tech-circles">
                            <span className="tech-circle ai">
                                <span className="material-icons">psychology</span>
                            </span>
                            <span className="tech-circle web">
                                <span className="material-icons">code</span>
                            </span>
                            <span className="tech-circle data">
                                <span className="material-icons">analytics</span>
                            </span>
                            <span className="tech-circle cloud">
                                <span className="material-icons">cloud</span>
                            </span>
                            <span className="tech-circle security">
                                <span className="material-icons">security</span>
                            </span>
                        </div>
                    </div>
                </div>
            </div>

            <div className="hero-scroll">
                <a href="#courses" className="scroll-indicator">
                    <span className="material-icons">keyboard_arrow_down</span>
                    <span>Explore our programs</span>
                </a>
                {/* <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="#2563eb">
                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.94-.49-7-3.85-7-7.93s3.05-7.44 7-7.93v15.86zm2-15.86c1.03.13 2 .45 2.87.93H13v-.93zM13 7h5.24c.25.31.48.65.68 1H13V7zm0 3h6.74c.08.33.15.66.19 1H13v-1zm0 9.93V19h2.87c-.87.48-1.84.8-2.87.93zM18.24 17H13v-1h5.92c-.2.35-.43.69-.68 1zm1.5-3H13v-1h6.93c-.04.34-.11.67-.19 1z"/>
                </svg> */}
            </div>
        </section>
    );
};

export default Hero;
