import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/CourseSection.css';

const CourseSection = () => {
    useEffect(() => {
        // Hide some courses initially if on homepage
        if (!window.location.pathname.includes('/courses')) {
            const courseCards = document.querySelectorAll('.course-card');
            if (courseCards.length >= 4) {
                courseCards[3].style.display = 'none';
                courseCards[4].style.display = 'none';
                courseCards[5].style.display = 'none';
            }
        }
    }, []);

    const showAllCourses = () => {
        const courseCards = document.querySelectorAll('.course-card');
        courseCards.forEach(card => {
            card.style.display = 'flex';
        });

        const viewAllBtn = document.getElementById('viewAllCoursesBtn');
        if (viewAllBtn) {
            viewAllBtn.textContent = 'All Courses Displayed';
            viewAllBtn.classList.add('btn-disabled');
            viewAllBtn.onclick = null;
        }

        // Scroll to show newly displayed courses
        if (courseCards[3]) {
            setTimeout(() => {
                courseCards[3].scrollIntoView({ behavior: 'smooth', block: 'center' });
            }, 100);
        }
    };

    return (
        <>
            <div className="cards course-grid">
                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/web3.png" alt="Web Development Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 12 Weeks</div>
                            <div><span className="material-icons">event</span> Starts Jun 1</div>
                        </div>
                        <h3>Full-Stack Web Development</h3>
                        <p>Master both front-end and back-end technologies to build complete web applications. Learn HTML, CSS, JavaScript, React, Node.js, and database integration.</p>
                        <Link to="/courses/web-dev" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-primary">Web Development</span>
                        <span className="course-price">¥350,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/data3.png" alt="Data Science Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 16 Weeks</div>
                            <div><span className="material-icons">event</span> Starts May 15</div>
                        </div>
                        <h3>Data Science & Analytics</h3>
                        <p>Learn to analyze and interpret complex data sets using Python, R, SQL, and visualization tools. Includes machine learning fundamentals and real-world projects.</p>
                        <Link to="/courses/data-science" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-success">Data Science</span>
                        <span className="course-price">¥420,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/cyber1.png" alt="Cybersecurity Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 14 Weeks</div>
                            <div><span className="material-icons">event</span> Starts Jun 15</div>
                        </div>
                        <h3>Cybersecurity Specialist</h3>
                        <p>Develop skills to protect systems and networks from digital attacks. Learn threat detection, ethical hacking, security protocols, and incident response.</p>
                        <Link to="/courses/cybersecurity" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-danger">Cybersecurity</span>
                        <span className="course-price">¥400,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/cloud1.png" alt="Cloud Computing Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 10 Weeks</div>
                            <div><span className="material-icons">event</span> Starts Jul 5</div>
                        </div>
                        <h3>Cloud Computing & DevOps</h3>
                        <p>Master cloud platforms like AWS, Azure, and Google Cloud. Learn infrastructure as code, containerization, CI/CD pipelines, and microservices architecture.</p>
                        <Link to="/courses/cloud-computing" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-info">Cloud Computing</span>
                        <span className="course-price">¥380,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/AI2.png" alt="AI Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 18 Weeks</div>
                            <div><span className="material-icons">event</span> Starts Jun 10</div>
                        </div>
                        <h3>Artificial Intelligence & Machine Learning</h3>
                        <p>Dive deep into AI algorithms, neural networks, deep learning, and natural language processing. Build intelligent systems that learn and adapt.</p>
                        <Link to="/courses/ai-ml" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-secondary">AI/ML</span>
                        <span className="course-price">¥450,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/mob1.png" alt="Mobile Development Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons">access_time</span> 12 Weeks</div>
                            <div><span className="material-icons">event</span> Starts Jul 15</div>
                        </div>
                        <h3>Mobile App Development</h3>
                        <p>Create native and cross-platform mobile applications. Master React Native, Flutter, iOS and Android development, and app deployment strategies.</p>
                        <Link to="/courses/mobile-dev" className="btn btn-outline">View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-warning">Mobile Dev</span>
                        <span className="course-price">¥370,000</span>
                    </div>
                </div>
            </div>

            {!window.location.pathname.includes('/courses') && (
                <div className="view-all-container">
                    <button className="btn btn-primary" id="viewAllCoursesBtn" onClick={showAllCourses}>
                        View All Courses
                    </button>
                </div>
            )}
        </>
    );
};

export default CourseSection;
