import React, { useState } from 'react';

const LoginModal = ({ isOpen, onClose }) => {
    const [isStudent, setIsStudent] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);

    const handleLogin = () => {
        // Handle login logic here
        console.log('Logging in:', { email, password, isStudent, rememberMe });
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <button className="modal-close" onClick={onClose}>&times;</button>
                <div className="modal-header">
                    <h2>Login to Portal</h2>
                </div>
                <div className="tabs">
                    <div className={`tab ${isStudent ? 'active' : ''}`} onClick={() => setIsStudent(true)}>Student</div>
                    <div className={`tab ${!isStudent ? 'active' : ''}`} onClick={() => setIsStudent(false)}>Faculty</div>
                </div>
                <form onSubmit={(e) => { e.preventDefault(); handleLogin(); }}>
                    <div className="form-group">
                        <label htmlFor="email">{isStudent ? 'Email Address' : 'Faculty Email'}</label>
                        <input
                            type="email"
                            id="email"
                            className="form-input"
                            placeholder={isStudent ? 'your.email@example.com' : 'faculty.email@example.com'}
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            className="form-input"
                            placeholder="••••••••"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <div className="form-check">
                            <input
                                type="checkbox"
                                id="remember"
                                checked={rememberMe}
                                onChange={() => setRememberMe(!rememberMe)}
                            />
                            <label htmlFor="remember">Remember me</label>
                        </div>
                    </div>
                    <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>Log In</button>
                    <div className="form-footer">
                        <p>Forgot your password? <a href="#">Reset it here</a></p>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default LoginModal;
