import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';



const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const [language, setLanguage] = useState('EN');

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const toggleLanguage = () => {
        setLanguage(language === 'EN' ? 'FR' : 'EN');
    };

    const openModal = () => {
        // Modal opening logic would go here
        console.log('Open login modal');
    };

    return (
        <header>
            <div className="container nav-container">
                <div className="logo">
                    <span className="material-icons logo-icon">school</span>
                    <span>Forum Information Academy</span>
                </div>
                <button className="hamburger" onClick={() => toggleMenu()}>
                    <span className="material-icons">menu</span>
                </button>
                <nav className="nav-menu" id="navMenu">
                    <ul>
                        <li><Link to="/courses">Courses</Link></li>
                        <li><Link to="/about">About Us</Link></li>
                        <li><Link to="/news">News & Events</Link></li>
                        <li><Link to="/apply">Apply Now</Link></li>
                        <li><Link to="/contact">Contact</Link></li>
                    </ul>
                </nav>
                <div className="auth-buttons">
                    <div className="lang-switch" onClick={() => toggleLanguage()}>
                        <span id="currentLang">EN</span>
                        <span className="material-icons">language</span>
                    </div>
                    <button className="btn btn-outline" onClick={() => openModal()}>Log In</button>
                    <Link to="/apply" className="btn btn-primary">Apply Now</Link>
                </div>
            </div>
        </header>
    );
};

export default Header;
