import React, { useEffect, useState } from 'react';
import '../styles/PreLoader.css';

const Preloader = () => {
    const [isLottieLoaded, setIsLottieLoaded] = useState(false);
    const [loadingTips, setLoadingTips] = useState(0);

    // Tips to display during loading
    const tips = [
        "Over 93% of our graduates secure jobs in tech within 3 months.",
        "Our curriculum is developed with input from over 50 industry partners.",
        "Take advantage of 1-on-1 mentorship with industry professionals.",
        "Need help? Our support team is available 24/7."
    ];

    // Rotate through tips
    useEffect(() => {
        const tipInterval = setInterval(() => {
            setLoadingTips(prev => (prev + 1) % tips.length);
        }, 3500);

        return () => clearInterval(tipInterval);
    }, []);

    // Load Lottie script
    useEffect(() => {
        if (!document.querySelector('script[src*="dotlottie-player"]')) {
            const script = document.createElement('script');
            script.src = 'https://unpkg.com/@dotlottie/player-component@latest/dist/dotlottie-player.mjs';
            script.type = 'module';
            script.onload = () => setIsLottieLoaded(true);
            document.head.appendChild(script);
        } else {
            setIsLottieLoaded(true);
        }
    }, []);

    return (
        <div id="loader-wrapper">
            <div className="background-grid"></div>

            <div className="loader-content">
                <div className="loader-logo">
                    <img src="/images/logo.png" alt="Forum Information Academy" className="academy-logo" />
                </div>

                <div className="lottie-container">
                    {isLottieLoaded && (
                        <dotlottie-player 
                            src="https://lottie.host/13e7ecff-228e-457e-a545-739689384141/SkRM7Futjq.lottie"
                            background="transparent" 
                            speed="1" 
                            style={{ width: '300px', height: '300px' }} 
                            loop 
                            autoplay>
                        </dotlottie-player>
                    )}
                </div>

                <div className="loader-message">Preparing your educational journey...</div>

                <div className="loader-tip">
                    <div className="tip-icon">
                        <span className="material-icons">tips_and_updates</span>
                    </div>
                    <p className="tip-text">{tips[loadingTips]}</p>
                </div>

                <div className="loading-bar-container">
                    <div className="loading-bar"></div>
                </div>
            </div>
        </div>
    );
};

export default Preloader;
