import React, { useState, useEffect, useRef } from 'react';
import { BarChart3, Briefcase, Users, Award, GraduationCap } from 'lucide-react';
import '../styles/StatsSection.css'; // Assuming you have a CSS file for styling

const EnhancedStatsSection = () => {
    const [isVisible, setIsVisible] = useState(false);
    const sectionRef = useRef(null);
    const canvasRef = useRef(null);
    const animationRef = useRef(null);

    // Animated counter hook
    const useCounter = (end, duration = 2000, start = 0) => {
        const [count, setCount] = useState(start);
        const countRef = useRef(start);
        const frameRef = useRef(0);
        const startTimeRef = useRef(0);

        useEffect(() => {
        if (!isVisible) return;

        const animate = (timestamp) => {
            if (!startTimeRef.current) startTimeRef.current = timestamp;
            const progress = timestamp - startTimeRef.current;
            const increment = Math.min(progress / duration, 1);
            const currentCount = Math.floor(start + (end - start) * easeOutCubic(increment));

            countRef.current = currentCount;
            setCount(currentCount);

            if (progress < duration) {
            frameRef.current = requestAnimationFrame(animate);
            }
        };

        frameRef.current = requestAnimationFrame(animate);

        return () => {
            cancelAnimationFrame(frameRef.current);
        };
        }, [end, duration, start, isVisible]);

        return count;
    };

    // Easing function for smoother animation
    const easeOutCubic = (x) => {
        return 1 - Math.pow(1 - x, 3);
    };

    const statsData = [
        {
        value: 95,
        suffix: '%',
        label: 'Job Placement Rate',
        icon: <Briefcase size={32} />,
        color: '#3b82f6'
        },
        {
        value: 50,
        suffix: '+',
        label: 'Industry Partners',
        icon: <Users size={32} />,
        color: '#10b981'
        },
        {
        value: 15,
        suffix: '+',
        label: 'Specialized Programs',
        icon: <Award size={32} />,
        color: '#8b5cf6'
        },
        {
        value: 2000,
        suffix: '+',
        label: 'Graduates',
        icon: <GraduationCap size={32} />,
        color: '#f59e0b'
        }
    ];

    // Particle animation for the background
    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        const particles = [];
        const particleCount = 70;

        // Set canvas dimensions
        const resizeCanvas = () => {
        canvas.width = canvas.offsetWidth;
        canvas.height = canvas.offsetHeight;
        };

        window.addEventListener('resize', resizeCanvas);
        resizeCanvas();

        // Create particles
        for (let i = 0; i < particleCount; i++) {
        particles.push({
            x: Math.random() * canvas.width,
            y: Math.random() * canvas.height,
            radius: Math.random() * 3 + 1,
            color: `rgba(${Math.floor(Math.random() * 100 + 155)}, ${Math.floor(Math.random() * 100 + 155)}, ${Math.floor(Math.random() * 100 + 155)}, 0.${Math.floor(Math.random() * 5 + 2)})`,
            speedX: Math.random() * 0.5 - 0.25,
            speedY: Math.random() * 0.5 - 0.25,
            maxConnections: 5
        });
        }

        const animate = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Update and draw particles
        for (let i = 0; i < particles.length; i++) {
            const p = particles[i];

            p.x += p.speedX;
            p.y += p.speedY;

            // Bounce on edges
            if (p.x < 0 || p.x > canvas.width) p.speedX *= -1;
            if (p.y < 0 || p.y > canvas.height) p.speedY *= -1;

            // Draw particle
            ctx.beginPath();
            ctx.arc(p.x, p.y, p.radius, 0, Math.PI * 2);
            ctx.fillStyle = p.color;
            ctx.fill();

            // Connect nearby particles
            let connections = 0;
            for (let j = i + 1; j < particles.length; j++) {
            if (connections >= p.maxConnections) break;

            const p2 = particles[j];
            const dx = p.x - p2.x;
            const dy = p.y - p2.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance < 100) {
                connections++;
                ctx.beginPath();
                ctx.moveTo(p.x, p.y);
                ctx.lineTo(p2.x, p2.y);
                ctx.strokeStyle = `rgba(100, 130, 255, ${0.2 * (1 - distance / 100)})`;
                ctx.lineWidth = 0.5;
                ctx.stroke();
            }
            }
        }

        animationRef.current = requestAnimationFrame(animate);
        };

        animate();

        return () => {
        cancelAnimationFrame(animationRef.current);
        window.removeEventListener('resize', resizeCanvas);
        };
    }, []);

    // Intersection Observer to trigger animations when section is visible
    useEffect(() => {
        const observer = new IntersectionObserver(
        (entries) => {
            entries.forEach((entry) => {
            if (entry.isIntersecting) {
                setIsVisible(true);
                observer.unobserve(entry.target);
            }
            });
        },
        { threshold: 0.1 }
        );

        if (sectionRef.current) {
        observer.observe(sectionRef.current);
        }

        return () => {
        if (sectionRef.current) {
            observer.unobserve(sectionRef.current);
        }
        };
    }, []);

    return (
        <section 
        ref={sectionRef}
        className="relative py-24 overflow-hidden bg-gradient-to-br from-slate-50 to-blue-50"
        style={{ minHeight: '600px' }}
        >
        <canvas 
            ref={canvasRef}
            className="absolute inset-0 w-full h-full"
            style={{ zIndex: 1 }}
        />

        {/* Decorative elements */}
        <div className="absolute top-0 left-0 w-64 h-64 rounded-full bg-blue-400 opacity-5" 
            style={{ transform: 'translate(-30%, -30%)' }} />
        <div className="absolute bottom-0 right-0 w-80 h-80 rounded-full bg-purple-400 opacity-5"
            style={{ transform: 'translate(30%, 30%)' }} />

        <div className="relative px-6 mx-auto max-w-7xl z-10">
            <div className="text-center mb-16">
            <h2 className="inline-block mb-4 text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-800 to-indigo-600 relative">
                Our Impact in Numbers
                <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-24 h-1 bg-gradient-to-r from-blue-400 to-indigo-500 rounded-full"></span>
            </h2>
            <p className="max-w-2xl mx-auto mt-4 text-lg text-gray-600">
                Driving education excellence through measurable outcomes
            </p>
            </div>

            <div className="grid grid-cols-1 gap-8 md:grid-cols-2 lg:grid-cols-4">
            {statsData.map((stat, index) => {
                const animatedValue = useCounter(isVisible ? stat.value : 0, 2000, 0);

                return (
                <div 
                    key={index}
                    className={`relative p-8 bg-white rounded-2xl shadow-lg transform transition-all duration-700 hover:-translate-y-2 hover:shadow-xl overflow-hidden ${
                    isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'
                    }`}
                    style={{ 
                    transitionDelay: `${index * 150}ms`,
                    boxShadow: '0 10px 40px -10px rgba(0, 0, 0, 0.1), 0 4px 20px -5px rgba(0, 0, 0, 0.05)'
                    }}
                >
                    {/* Background gradient accent */}
                    <div 
                    className="absolute top-0 left-0 w-full h-2 rounded-t-lg" 
                    style={{ background: `linear-gradient(90deg, ${stat.color}, ${stat.color}BB)` }}
                    ></div>

                    <div className="flex flex-col items-center">
                    <div 
                        className="flex items-center justify-center w-16 h-16 mb-6 rounded-full"
                        style={{ backgroundColor: `${stat.color}15` }}
                    >
                        <div className="text-blue-500" style={{ color: stat.color }}>
                        {stat.icon}
                        </div>
                    </div>

                    <div className="flex items-center mb-2 text-5xl font-bold tracking-tight">
                        <span className="bg-clip-text text-transparent bg-gradient-to-r" 
                            style={{ backgroundImage: `linear-gradient(90deg, ${stat.color}, ${stat.color}BB)` }}>
                        {animatedValue}
                        </span>
                        <span className="ml-1 text-3xl font-semibold text-gray-700">{stat.suffix}</span>
                    </div>

                    <p className="mt-2 text-lg font-medium text-gray-600">{stat.label}</p>
                    </div>

                    {/* Decorative corner accent */}
                    <div className="absolute -bottom-6 -right-6 w-16 h-16 rounded-full opacity-10"
                        style={{ backgroundColor: stat.color }}></div>
                </div>
                );
            })}
            </div>
        </div>
        </section>
    );
};

export default EnhancedStatsSection;
