import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import Hero from '../components/Hero';
import CourseSection from '../components/CourseSection';
import AboutSection from '../components/AboutSection';
import TestimonialsSection from '../components/TestimonialsSection';
import StatsSection from '../components/StatsSection';
import NewsSection from '../components/NewsSection';
import CTASection from '../components/CTASection';
import ContactSection from '../components/ContactSection';
import '../styles/homepage.css';

const HomePage = () => {
    // Add animation effects when components enter viewport
    useEffect(() => {
        // If anime.js is available, add entry animations for sections
        if (window.anime) {
            const sections = document.querySelectorAll('.home-page .section');
            sections.forEach((section, index) => {
                // Observe when section enters viewport
                const observer = new IntersectionObserver((entries) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            // Animate section when it comes into view
                            window.anime({
                                targets: entry.target,
                                opacity: [0, 1],
                                translateY: [20, 0],
                                easing: 'easeOutQuad',
                                duration: 600,
                                delay: 100
                            });
                            observer.unobserve(entry.target);
                        }
                    });
                }, { threshold: 0.1 });

                observer.observe(section);
            });
        }
    }, []);

    return (
        <div className="home-page">
            {/* Hero Section */}
            <section className="hero">
                <div className="container">
                    <div className="hero-content">
                        <div className="hero-text">
                            <h1>Shape Your Future in Information Technology</h1>
                            <p>Gain cutting-edge skills and knowledge with our industry-focused IT programs at Forum Information Academy. Join a community of innovators shaping the digital future.</p>
                            <div className="hero-cta">
                                <Link to="/courses" className="btn btn-primary">Browse Courses</Link>
                                <Link to="/about" className="btn btn-outline" style={{borderColor: 'white', color: 'white'}}>Learn More</Link>
                            </div>
                        </div>
                        <div className="hero-image">
                            <img src="/images/AI1.png" alt="Students at Forum Information Academy" />
                        </div>
                    </div>
                </div>
            </section>

            {/* Course Catalog Section */}
            <section className="section" id="courses">
                <div className="container">
                    <div className="section-header">
                        <h2>Our Courses</h2>
                        <p>Explore our wide range of IT courses designed to prepare you for the careers of today and tomorrow.</p>
                    </div>

                    {/* Course filters */}
                    <div className="course-filters tag-list">
                        <div className="inner">
                            <button className="filter-btn tag active">
                                <span className="material-icons">school</span>
                                All Courses
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">code</span>
                                Web Development
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">analytics</span>
                                Data Science
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">security</span>
                                Cybersecurity
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">cloud</span>
                                Cloud Computing
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">smart_toy</span>
                                Artificial Intelligence
                            </button>
                            {/* Additional tags for animation */}
                            <button className="filter-btn tag">
                                <span className="material-icons">school</span>
                                All Courses
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">code</span>
                                Web Development
                            </button>
                        </div>
                        <div className="fade"></div>
                    </div>

                    <CourseSection />
                </div>
            </section>

            {/* About Section */}
            <section className="section" id="about" style={{backgroundColor: 'white'}}>
                <div className="container">
                    <div className="section-header">
                        <h2>Why Choose Forum Information Academy</h2>
                        <p>We're dedicated to providing cutting-edge IT education that prepares students for the real world.</p>
                    </div>
                    <AboutSection />
                </div>
            </section>

            {/* Testimonials Section */}
            <section className="section testimonials">
                <div className="container">
                    <div className="section-header">
                        <h2>What Our Students Say</h2>
                        <p>Hear from our graduates who have successfully launched their careers in the tech industry.</p>
                    </div>
                    <TestimonialsSection />
                </div>
            </section>

            {/* Stats Section */}
            <section className="section stats">
                <div className="container">
                    <StatsSection />
                </div>
            </section>

            {/* News & Events Section */}
            <section className="section news-events" id="news">
                <div className="container">
                    <div className="section-header">
                        <h2>News & Events</h2>
                        <p>Stay updated with the latest happenings at Forum Information Academy.</p>
                    </div>
                    <NewsSection />
                </div>
            </section>

            {/* CTA Section */}
            <section className="section cta-section">
                <div className="container">
                    <CTASection />
                </div>
            </section>

            {/* Contact Section */}
            <section className="section" id="contact" style={{backgroundColor: 'white'}}>
                <div className="container">
                    <div className="section-header">
                        <h2>Contact Us</h2>
                        <p>We're here to answer any questions you have about our programs and admissions process.</p>
                    </div>
                    <ContactSection />
                </div>
            </section>
        </div>
    );
};

export default HomePage;
