import React, { useEffect } from 'react';
import './Preloader.css';

const Preloader = () => {
    // Add a script to load the dotlottie player if it's not already loaded
    useEffect(() => {
        if (!document.querySelector('script[src*="dotlottie-player"]')) {
            const script = document.createElement('script');
            script.src = 'https://unpkg.com/@dotlottie/player-component@latest/dist/dotlottie-player.mjs';
            script.type = 'module';
            document.head.appendChild(script);
        }
    }, []);

    return (
        <div id="loader-wrapper">
            <div className="loader-content">
                <div className="loader-logo">
                    <span className="material-icons loader-logo-icon">school</span>
                    <span>Forum Information Academy</span>
                </div>
                <div className="loader-message">Preparing your educational journey...</div>
                <div style={{ display: 'flex', justifyContent: 'center', margin: '20px auto' }}>
                    <dotlottie-player 
                        src="https://lottie.host/13e7ecff-228e-457e-a545-739689384141/SkRM7Futjq.lottie"
                        background="transparent" 
                        speed="1" 
                        style={{ width: '300px', height: '300px' }} 
                        loop 
                        autoplay>
                    </dotlottie-player>
                </div>
            </div>
        </div>
    );
};

export default Preloader;
