import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import LoginModal from './LoginModal';
import '../styles/Header.css';

const Header = () => {
    const [isLoginOpen, setIsLoginOpen] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const location = useLocation();

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const closeMenu = () => {
        setIsMenuOpen(false);
    };

    const isActive = (path) => {
        return location.pathname === path ? 'active' : '';
    };

    return (
        <header className="main-header">
            <div className="container">
                <div className="header-content">
                    <div className="logo">
                        <Link to="/">
                            <img src="/images/logo.png" alt="Forum Information Academy" />
                            <span>Forum Academy</span>
                        </Link>
                    </div>

                    <nav className={`main-nav ${isMenuOpen ? 'open' : ''}`}>
                        <ul>
                            <li><Link to="/" className={isActive('/')} onClick={closeMenu}>Home</Link></li>
                            <li><Link to="/programs" className={isActive('/programs')} onClick={closeMenu}>Programs</Link></li>
                            <li><Link to="/about" className={isActive('/about')} onClick={closeMenu}>About Us</Link></li>
                            <li><Link to="/news" className={isActive('/news')} onClick={closeMenu}>News</Link></li>
                            <li><Link to="/contact" className={isActive('/contact')} onClick={closeMenu}>Contact</Link></li>
                        </ul>
                    </nav>

                    <div className="header-actions">
                        <button 
                            className="btn btn-login"
                            onClick={() => {
                                console.log("Opening login modal");
                                setIsLoginOpen(true);
                            }}
                        >
                            <span className="material-icons">account_circle</span>
                            <span>Log In</span>
                        </button>

                        <Link to="/application" className="btn btn-apply">Apply Now</Link>

                        <button className="mobile-menu-toggle" onClick={toggleMenu}>
                            <span className="material-icons">
                                {isMenuOpen ? 'close' : 'menu'}
                            </span>
                        </button>
                    </div>
                </div>
            </div>

            {/* Add the LoginModal component here */}
            <LoginModal 
                isOpen={isLoginOpen}
                onClose={() => setIsLoginOpen(false)}
            />
        </header>
    );
};

export default Header;
