import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/CTASection.css';

const CTASection = ({ 
    title = "Launch Your Tech Career Today", 
    description = "Join thousands of successful graduates who transformed their lives with our industry-leading IT education programs.",
    buttonText = "Get Started",
    buttonLink = "/contact",
    backgroundImage = "/images/cta-background.jpg"
}) => {
    const sectionRef = useRef(null);

    // Animation on scroll
    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('visible');
                    // Add staggered animations for child elements
                    const elements = entry.target.querySelectorAll('.animate-in');
                    elements.forEach((el, index) => {
                        setTimeout(() => {
                            el.classList.add('visible');
                        }, 150 * index);
                    });
                }
            });
        }, { threshold: 0.2 });

        if (sectionRef.current) {
            observer.observe(sectionRef.current);
        }

        // Cleanup observer
        return () => {
            if (sectionRef.current) {
                observer.unobserve(sectionRef.current);
            }
        };
    }, []);

    return (
        <section 
            className="cta-section" 
            ref={sectionRef} 
            style={{backgroundImage: `url(${backgroundImage})`}}
        >
            <div className="overlay"></div>
            <div className="container">
                <div className="cta-content">
                    <div className="cta-card animate-in">
                        <div className="cta-text">
                            <h2 className="cta-title animate-in">{title}</h2>
                            <p className="cta-description animate-in">{description}</p>
                        </div>

                        <div className="cta-actions animate-in">
                            <Link to={buttonLink} className="cta-button pulse-effect">
                                {buttonText}
                                <span className="material-icons">arrow_forward</span>
                            </Link>
                        </div>

                        <div className="cta-highlights">
                            <div className="highlight-item animate-in">
                                <div className="highlight-icon">
                                    <span className="material-icons">school</span>
                                </div>
                                <div className="highlight-content">
                                    <h4>Industry-Recognized</h4>
                                    <p>Certifications included with all programs</p>
                                </div>
                            </div>

                            <div className="highlight-item animate-in">
                                <div className="highlight-icon">
                                    <span className="material-icons">work</span>
                                </div>
                                <div className="highlight-content">
                                    <h4>93% Job Placement</h4>
                                    <p>Career support from day one to hire</p>
                                </div>
                            </div>

                            <div className="highlight-item animate-in">
                                <div className="highlight-icon">
                                    <span className="material-icons">payments</span>
                                </div>
                                <div className="highlight-content">
                                    <h4>Flexible Payment</h4>
                                    <p>Multiple options to fit your budget</p>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="cta-image-container animate-in">
                        <div className="floating-badge">
                            <div className="badge-content">
                                <span className="badge-number">6x</span>
                                <span className="badge-text">Average Salary Increase</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default CTASection;
