import React, { useState, useEffect } from 'react';
import '../styles/LoginModal.css';

const LoginModal = ({ isOpen, onClose }) => {
    const [isStudent, setIsStudent] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [isVisible, setIsVisible] = useState(false);

    // Handle animation timing for modal appearance
    useEffect(() => {
        if (isOpen) {
            document.body.style.overflow = 'hidden';
            setTimeout(() => setIsVisible(true), 10);
        } else {
            setIsVisible(false);
            document.body.style.overflow = 'auto';
        }

        return () => {
            document.body.style.overflow = 'auto';
        };
    }, [isOpen]);

    // Close modal on escape key
    useEffect(() => {
        const handleEsc = (event) => {
            if (event.keyCode === 27) onClose();
        };
        window.addEventListener('keydown', handleEsc);

        return () => {
            window.removeEventListener('keydown', handleEsc);
        };
    }, [onClose]);

    const handleLogin = (e) => {
        e.preventDefault();
        // Handle login logic here
        console.log('Logging in:', { email, password, isStudent, rememberMe });
    };

    if (!isOpen) return null;

    // Helper function to return icon based on user type
    const getUserIcon = () => {
        return isStudent ? 
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="user-icon">
                <path d="M12 3L1 9l11 6 9-4.91V17c0 .55.45 1 1 1s1-.45 1-1v-6.7l-10-5.3zm7 14.96V16.5c0-2.49-2.01-4.5-4.5-4.5h-5c-2.49 0-4.5 2.01-4.5 4.5v1.46c0 .39.31.7.7.7h12.6c.39 0 .7-.31.7-.7z" />
            </svg>
            : 
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="user-icon">
                <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z" />
            </svg>;
    };

    return (
        <div className={`login-modal-overlay ${isVisible ? 'visible' : ''}`} onClick={onClose}>
            <div className="login-modal" onClick={e => e.stopPropagation()}>
                <button className="modal-close-btn" onClick={onClose} aria-label="Close login modal">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                    </svg>
                </button>

                <div className="login-modal-content">
                    <div className="login-side-panel">
                        <div className="login-branding">
                            <h2>Forum Info Academy</h2>
                            <p>Transforming careers through technology education</p>
                        </div>
                        <div className="login-illustration">
                            {/* Animated illustration */}
                            <div className="illustration-element circle-1"></div>
                            <div className="illustration-element circle-2"></div>
                            <div className="illustration-element square"></div>
                            <div className="illustration-element triangle"></div>
                        </div>
                    </div>

                    <div className="login-form-container">
                        <div className="login-header">
                            <div className="login-icon">
                                {getUserIcon()}
                            </div>
                            <h2>Welcome Back</h2>
                            <p>Log in to access your {isStudent ? 'student' : 'faculty'} portal</p>
                        </div>

                        <div className="login-tabs">
                            <button 
                                className={`login-tab ${isStudent ? 'active' : ''}`} 
                                onClick={() => setIsStudent(true)}
                            >
                                Student
                            </button>
                            <button 
                                className={`login-tab ${!isStudent ? 'active' : ''}`} 
                                onClick={() => setIsStudent(false)}
                            >
                                Faculty
                            </button>
                        </div>

                        <form className="login-form" onSubmit={handleLogin}>
                            <div className="form-group">
                                <label htmlFor="email">
                                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="input-icon">
                                        <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z" />
                                    </svg>
                                    {isStudent ? 'Student Email' : 'Faculty Email'}
                                </label>
                                <input
                                    type="email"
                                    id="email"
                                    placeholder={isStudent ? 'your.email@example.com' : 'faculty.email@example.com'}
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="password">
                                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="input-icon">
                                        <path d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zm-6 9c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm3.1-9H8.9V6c0-1.71 1.39-3.1 3.1-3.1 1.71 0 3.1 1.39 3.1 3.1v2z" />
                                    </svg>
                                    Password
                                </label>
                                <input
                                    type="password"
                                    id="password"
                                    placeholder="••••••••"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </div>

                            <div className="form-options">
                                <div className="remember-me">
                                    <input
                                        type="checkbox"
                                        id="remember"
                                        checked={rememberMe}
                                        onChange={() => setRememberMe(!rememberMe)}
                                    />
                                    <label htmlFor="remember">Remember me</label>
                                </div>
                                <a href="#" className="forgot-password">Forgot password?</a>
                            </div>

                            <button type="submit" className="login-button">
                                Log In
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="button-icon">
                                    <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z"/>
                                </svg>
                            </button>
                        </form>

                        <div className="login-help">
                            <p>Need help? <a href="#">Contact IT Support</a></p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginModal;
