import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Footer.css';

const Footer = () => {
    const currentYear = new Date().getFullYear();

    return (
        <footer className="site-footer">
            <div className="footer-top">
                <div className="container">
                    <div className="footer-logo-section">
                        <Link to="/" className="footer-logo">
                            <img src="/images/logo-white.png" alt="Forum Information Academy" />
                        </Link>
                        <p className="footer-tagline">
                            Empowering careers through cutting-edge IT education
                        </p>
                        <div className="footer-social">
                            <a href="https://facebook.com/" target="_blank" rel="noopener noreferrer" aria-label="Facebook">
                                <span className="material-icons">facebook</span>
                            </a>
                            <a href="https://twitter.com/" target="_blank" rel="noopener noreferrer" aria-label="Twitter">
                                <span className="material-icons">alternate_email</span>
                            </a>
                            <a href="https://linkedin.com/" target="_blank" rel="noopener noreferrer" aria-label="LinkedIn">
                                <span className="material-icons">work</span>
                            </a>
                            <a href="https://instagram.com/" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
                                <span className="material-icons">photo_camera</span>
                            </a>
                            <a href="https://youtube.com/" target="_blank" rel="noopener noreferrer" aria-label="YouTube">
                                <span className="material-icons">smart_display</span>
                            </a>
                        </div>
                    </div>

                    <div className="footer-grid">
                        <div className="footer-column">
                            <h3>Main Navigation</h3>
                            <ul className="footer-links">
                                <li><Link to="/">Home</Link></li>
                                <li><Link to="/courses">Courses</Link></li>
                                <li><Link to="/about">About Us</Link></li>
                                <li><Link to="/news">News & Events</Link></li>
                                <li><Link to="/apply">Apply Now</Link></li>
                                <li><Link to="/contact">Contact Us</Link></li>
                            </ul>
                        </div>

                        <div className="footer-column">
                            <h3>Our Courses</h3>
                            <ul className="footer-links">
                                <li><Link to="/courses/web-development">Web Development</Link></li>
                                <li><Link to="/courses/data-science">Data Science</Link></li>
                                <li><Link to="/courses/cybersecurity">Cybersecurity</Link></li>
                                <li><Link to="/courses/cloud-computing">Cloud Computing</Link></li>
                                <li><Link to="/courses/ai-ml">AI & Machine Learning</Link></li>
                            </ul>
                        </div>

                        <div className="footer-column">
                            <h3>Quick Links</h3>
                            <ul className="footer-links">
                                <li><Link to="/faq">FAQ</Link></li>
                                <li><Link to="/testimonials">Student Success</Link></li>
                                <li><Link to="/scholarships">Scholarships</Link></li>
                                <li><Link to="/partners">Industry Partners</Link></li>
                                <li><Link to="/events">Upcoming Events</Link></li>
                            </ul>
                        </div>

                        <div className="footer-column">
                            <h3>Contact Us</h3>
                            <ul className="footer-contact">
                                <li>
                                    <span className="material-icons">location_on</span>
                                    <div>
                                        123 Education Street<br />
                                        City, State 12345
                                    </div>
                                </li>
                                <li>
                                    <span className="material-icons">phone</span>
                                    <div>
                                        Main: (123) 456-7890<br />
                                        Support: (123) 456-7891
                                    </div>
                                </li>
                                <li>
                                    <span className="material-icons">email</span>
                                    <div>
                                        <a href="mailto:info@example.com">info@example.com</a><br />
                                        <a href="mailto:support@example.com">support@example.com</a>
                                    </div>
                                </li>
                                <li>
                                    <span className="material-icons">schedule</span>
                                    <div>
                                        Mon-Fri: 9:00 AM - 6:00 PM<br />
                                        Sat: 10:00 AM - 2:00 PM
                                    </div>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>

            <div className="footer-middle">
                <div className="container">
                    <div className="newsletter">
                        <div className="newsletter-text">
                            <h3>Subscribe To Our Newsletter</h3>
                            <p>Get the latest news, course updates, and special offers delivered directly to your inbox.</p>
                        </div>
                        <form className="newsletter-form">
                            <input type="email" placeholder="Your email address" required />
                            <button type="submit">Subscribe</button>
                        </form>
                    </div>
                </div>
            </div>

            <div className="footer-bottom">
                <div className="container">
                    <div className="footer-bottom-content">
                        <p className="copyright">
                            &copy; {currentYear} Forum Information Academy. All rights reserved.
                        </p>
                        <div className="footer-legal-links">
                            <a href="/privacy-policy">Privacy Policy</a>
                            <a href="/terms">Terms of Service</a>
                        </div>
                    </div>
                </div>
            </div>
        </footer>
    );
};

export default Footer;
