import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

const CourseSection = () => {
    useEffect(() => {
        // Hide some courses initially
        const courseCards = document.querySelectorAll('.course-card');
        if (courseCards.length >= 4) {
            courseCards[3].style.display = 'none';
            courseCards[4].style.display = 'none';
            courseCards[5].style.display = 'none';
        }
    }, []);

    const showAllCourses = () => {
        const courseCards = document.querySelectorAll('.course-card');
        courseCards.forEach(card => {
            card.style.display = 'flex';
        });

        const viewAllBtn = document.getElementById('viewAllCoursesBtn');
        if (viewAllBtn) {
            viewAllBtn.textContent = 'All Courses Displayed';
            viewAllBtn.classList.add('btn-disabled');
            viewAllBtn.onclick = null;
        }

        // Scroll to show newly displayed courses
        if (courseCards[3]) {
            setTimeout(() => {
                courseCards[3].scrollIntoView({ behavior: 'smooth', block: 'center' });
            }, 100);
        }
    };

    return (
        <>
            <div className="cards">
                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/web3.png" alt="Web Development Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons" style={{fontSize: '16px'}}>access_time</span> 12 Weeks</div>
                            <div><span className="material-icons" style={{fontSize: '16px'}}>event</span> Starts Jun 1</div>
                        </div>
                        <h3>Full-Stack Web Development</h3>
                        <p>Master both front-end and back-end technologies to build complete web applications. Learn HTML, CSS, JavaScript, React, Node.js, and database integration.</p>
                        <Link to="/courses/web-dev" className="btn btn-outline" style={{marginTop: 'auto'}}>View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-primary">Web Development</span>
                        <span>¥350,000</span>
                    </div>
                </div>

                <div className="card course-card">
                    <div className="card-img">
                        <img src="/images/data3.png" alt="Data Science Course" />
                    </div>
                    <div className="card-body">
                        <div className="course-meta">
                            <div><span className="material-icons" style={{fontSize: '16px'}}>access_time</span> 16 Weeks</div>
                            <div><span className="material-icons" style={{fontSize: '16px'}}>event</span> Starts May 15</div>
                        </div>
                        <h3>Data Science & Analytics</h3>
                        <p>Learn to analyze and interpret complex data sets using Python, R, SQL, and visualization tools. Includes machine learning fundamentals and real-world projects.</p>
                        <Link to="/courses/data-science" className="btn btn-outline" style={{marginTop: 'auto'}}>View Details</Link>
                    </div>
                    <div className="card-footer">
                        <span className="badge badge-success">Data Science</span>
                        <span>¥420,000</span>
                    </div>
                </div>

                {/* Add more course cards here */}
                {/* ... */}
            </div>
            <div style={{textAlign: 'center', marginTop: '2rem'}}>
                <button className="btn btn-primary" id="viewAllCoursesBtn" onClick={showAllCourses}>View All Courses</button>
            </div>
        </>
    );
};

export default CourseSection;
