import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/CTASection.css';

const CTASection = ({ 
    title = "Ready to Start Your IT Career?", 
    description = "Contact our admissions team to learn more about our programs, tuition assistance options, and how we can help you achieve your career goals.",
    buttonText = "Contact Us Today",
    buttonLink = "/contact",
    backgroundImage = "/images/cta-background.jpg"
}) => {
    const sectionRef = useRef(null);

    // Animation on scroll
    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('visible');
                }
            });
        }, { threshold: 0.1 });

        if (sectionRef.current) {
            observer.observe(sectionRef.current);
        }

        // Cleanup observer
        return () => {
            if (sectionRef.current) {
                observer.unobserve(sectionRef.current);
            }
        };
    }, []);

    return (
        <section 
            className="cta-section" 
            ref={sectionRef} 
            style={{backgroundImage: `url(${backgroundImage})`}}
        >
            <div className="overlay"></div>
            <div className="container">
                <div className="cta-content">
                    <div className="cta-text">
                        <h2>{title}</h2>
                        <p>{description}</p>
                    </div>
                    <div className="cta-actions">
                        <Link to={buttonLink} className="cta-button">
                            {buttonText}
                            <span className="material-icons">arrow_forward</span>
                        </Link>
                        <div className="cta-features">
                            <div className="feature">
                                <span className="material-icons">school</span>
                                <span>Industry-recognized certifications</span>
                            </div>
                            <div className="feature">
                                <span className="material-icons">work</span>
                                <span>93% job placement success</span>
                            </div>
                            <div className="feature">
                                <span className="material-icons">payments</span>
                                <span>Flexible payment options</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default CTASection;
