import React, { useState, useEffect, useRef } from 'react';
import '../styles/StatsSection.css'; // Assuming you have a CSS file for styling

const StatsSection = () => {
    const statsData = [
        {
            value: 95,
            suffix: '%',
            label: 'Job Placement Rate',
            icon: 'fa-solid fa-briefcase'
        },
        {
            value: 50,
            suffix: '+',
            label: 'Industry Partners',
            icon: 'fa-solid fa-handshake'
        },
        {
            value: 15,
            suffix: '+',
            label: 'Specialized Programs',
            icon: 'fa-solid fa-graduation-cap'
        },
        {
            value: 2000,
            suffix: '+',
            label: 'Graduates',
            icon: 'fa-solid fa-user-graduate'
        }
    ];

    return (
        <section className="stats-section">
            <div className="stats-bg-pattern"></div>
            <div className="stats-overlay"></div>
            <div className="container">
                <div className="stats-header">
                    <h2>Our Impact in Numbers</h2>
                    <p>Driving education excellence through measurable outcomes</p>
                </div>
                <div className="stats-grid">
                    {statsData.map((stat, index) => (
                        <div 
                            className="stat-item" 
                            key={index} 
                            data-aos="fade-up" 
                            data-aos-delay={index * 100}
                        >
                            <div className="stat-icon">
                                <i className={stat.icon}></i>
                            </div>
                            <div className="stat-value">
                                <span className="counter">{stat.value}</span>
                                <span className="suffix">{stat.suffix}</span>
                            </div>
                            <p className="stat-label">{stat.label}</p>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default StatsSection;
