import ContactSection from '../components/ContactSection';
import ThreeJSBackground from '../components/ThreeJSBackground';
import '../styles/HomePage.css';

const HomePage = () => {
    // Add animation effects when components enter viewport
    useEffect(() => {
        // If anime.js is available, add entry animations for sections
        if (window.anime) {
            const sections = document.querySelectorAll('.home-page .section');
            sections.forEach((section) => {
                // Observe when section enters viewport
                const observer = new IntersectionObserver((entries) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            // Animate section when it comes into view
                            window.anime({
                                targets: entry.target,
                                opacity: [0, 1],
                                translateY: [20, 0],
                                easing: 'easeOutQuad',
                                duration: 600,
                                delay: 100
                            });
                            observer.unobserve(entry.target);
                        }
                    });
                }, { threshold: 0.1 });

                observer.observe(section);
            });
        }
    }, []);

    return (
        <div className="home-page">
            {/* Three.js Background */}
            <ThreeJSBackground />

            {/* Hero Section */}
            <section className="section" id="hero">
                <div className="hero-content">
                    <h1>Forum Information Academy</h1>
                    <p>Empowering the next generation of tech professionals with cutting-edge IT education</p>
                    <Link to="/apply" className="cta-button">Apply Now</Link>
                </div>
            </section>

            {/* Course Catalog Section - Key Feature 1 */}
            <section className="section" id="courses">
                <div className="container">
                    <div className="section-header">
                        <h2>Our Courses</h2>
                        <p>Explore our wide range of IT courses designed to prepare you for the careers of today and tomorrow.</p>
                    </div>

                    {/* Course filters */}
                    <div className="course-filters tag-list">
                        <div className="inner">
                            <button className="filter-btn tag active">
                                <span className="material-icons">school</span>
                                All Courses
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">code</span>
                                Web Development
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">analytics</span>
                                Data Science
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">security</span>
                                Cybersecurity
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">cloud</span>
                                Cloud Computing
                            </button>
                            <button className="filter-btn tag">
                                <span className="material-icons">smart_toy</span>
                                Artificial Intelligence
                            </button>
                        </div>
                        <div className="fade"></div>
                    </div>

                    <CourseSection />
                </div>
            </section>

            {/* About Section - Key Feature 2 */}
            <section className="section" id="about">
                <div className="container">
                    <div className="section-header">
                        <h2>Why Choose Forum Information Academy</h2>
                        <p>We're dedicated to providing cutting-edge IT education that prepares students for the real world.</p>
                    </div>
                    <AboutSection />
                </div>
            </section>

            {/* Stats Section */}
            <section className="section" id="stats">
                <div className="container">
                    <div className="section-header">
                        <h2>Our Impact</h2>
                        <p>The numbers that define our success in preparing the next generation of tech professionals.</p>
                    </div>
                    <StatsSection />
                </div>
            </section>

            {/* Testimonials Section - Key Feature 3 */}
            <section className="section" id="testimonials">
                <div className="container">
                    <div className="section-header">
                        <h2>Student Success Stories</h2>
                        <p>Hear from our graduates who have successfully launched their careers in the tech industry.</p>
                    </div>
                    <div className="testimonials-content">
                        <TestimonialsSection />
                    </div>
                </div>
            </section>

            {/* News Section */}
            <section className="section" id="news">
                <div className="container">
                    <div className="section-header">
                        <h2>Latest News & Events</h2>
                        <p>Stay updated with our latest announcements, events, and tech industry insights.</p>
                    </div>
                    <NewsSection />
                </div>
            </section>

            {/* Contact Section */}
            <section className="section" id="contact">
                <div className="container">
                    <div className="section-header">
                        <h2>Get In Touch</h2>
                        <p>Have questions? We're here to help you start your journey in tech education.</p>
                    </div>
                    <ContactSection />
                </div>
            </section>
        </div>
    );
};

export default HomePage;
