import React from 'react';
import Hero from '../components/Hero';
import CourseSection from '../components/CourseSection';
import AboutSection from '../components/AboutSection';
import TestimonialsSection from '../components/TestimonialsSection';
import StatsSection from '../components/StatsSection';
import NewsSection from '../components/NewsSection';
import CTASection from '../components/CTASection';
import ContactSection from '../components/ContactSection';
import '../styles/homepage.css'; // Ensure you have the correct path to your CSS file


const HomePage = () => {
    return (
        <div className="home-page">
            <section className="hero">
                <div className="container">
                    <Hero />
                </div>
            </section>

            <section className="section">
                <div className="container">
                    <div className="section-header">
                        <h2>Our Popular Courses</h2>
                        <p>Explore our wide range of courses designed to help you advance your career and achieve your goals.</p>
                    </div>
                    <CourseSection />
                </div>
            </section>

            <section className="section" style={{backgroundColor: 'white'}}>
                <div className="container">
                    <AboutSection />
                </div>
            </section>

            <section className="section testimonials">
                <div className="container">
                    <div className="section-header">
                        <h2>What Our Students Say</h2>
                        <p>Hear from our students about their learning experience with Forum Academy.</p>
                    </div>
                    <TestimonialsSection />
                </div>
            </section>

            <section className="section stats">
                <div className="container">
                    <StatsSection />
                </div>
            </section>

            <section className="section news-events">
                <div className="container">
                    <div className="section-header">
                        <h2>Latest News & Events</h2>
                        <p>Stay updated with our latest announcements, events, and educational resources.</p>
                    </div>
                    <NewsSection />
                </div>
            </section>

            <section className="section cta-section">
                <div className="container">
                    <CTASection />
                </div>
            </section>

            <section className="section" style={{backgroundColor: 'white'}}>
                <div className="container">
                    <ContactSection />
                </div>
            </section>
        </div>
    );
};

export default HomePage;
