import React, {  useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/Header.css';

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const [language, setLanguage] = useState('EN');
    const [scrolled, setScrolled] = useState(false);
    const [dropdownOpen, setDropdownOpen] = useState('');

    const location = useLocation();

    // Close mobile menu when route changes
    useEffect(() => {
        setMenuOpen(false);
        setDropdownOpen('');
    }, [location]);

    // Add scroll listener for header style change
    useEffect(() => {
        const handleScroll = () => {
            const isScrolled = window.scrollY > 50;
            if (isScrolled !== scrolled) {
                setScrolled(isScrolled);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [scrolled]);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const toggleLanguage = () => {
        setLanguage(language === 'EN' ? 'JP' : 'EN');
    };

    const toggleDropdown = (menu) => {
        setDropdownOpen(dropdownOpen === menu ? '' : menu);
    };

    const openModal = () => {
        // Modal opening logic would go here
        console.log('Open login modal');
    };

    return (
        <header className={`site-header ${scrolled ? 'scrolled' : ''}`}>
            <div className="container header-container">
                <Link to="/" className="header-logo">
                    <img src="/images/logo.png" alt="Forum Information Academy" className="logo-image" />
                    <img src="/images/logo-white.png" alt="Forum Information Academy" className="logo-image-white" />
                </Link>

                <button 
                    className={`menu-toggle ${menuOpen ? 'active' : ''}`} 
                    onClick={toggleMenu}
                    aria-label="Toggle menu"
                    aria-expanded={menuOpen}
                >
                    <span className="menu-toggle-bar"></span>
                    <span className="menu-toggle-bar"></span>
                    <span className="menu-toggle-bar"></span>
                </button>

                <div className={`header-nav-wrapper ${menuOpen ? 'open' : ''}`}>
                    <nav className="header-nav">
                        <ul className="nav-list">
                            <li className="nav-item has-dropdown">
                                <button 
                                    className="nav-link dropdown-trigger"
                                    onClick={() => toggleDropdown('courses')}
                                    aria-expanded={dropdownOpen === 'courses'}
                                >
                                    Programs
                                    <span className="material-icons">expand_more</span>
                                </button>
                                <div className={`dropdown-menu ${dropdownOpen === 'courses' ? 'open' : ''}`}>
                                    <div className="dropdown-column">
                                        <h4>Featured Programs</h4>
                                        <ul className="dropdown-list">
                                            <li>
                                                <Link to="/courses/web-development" className="dropdown-link">
                                                    <span className="material-icons">web</span>
                                                    <div>
                                                        <span>Web Development</span>
                                                        <small>Full-stack engineering courses</small>
                                                    </div>
