import React from 'react';
import { Link } from 'react-router-dom';

const Hero = () => {
    return (
        <div className="hero-content">
            <div className="hero-text">
                <h1>Shape Your Future in Information Technology</h1>
                <p>Gain cutting-edge skills and knowledge with our industry-focused IT programs at Forum Information Academy. Join a community of innovators shaping the digital future.</p>
                <div className="hero-cta">
                    <Link to="/courses" className="btn btn-primary">Browse Courses</Link>
                    <Link to="/about" className="btn btn-outline" style={{borderColor: 'white', color: 'white'}}>Learn More</Link>
                </div>
            </div>
            <div className="hero-image">
                <img src="/images/AI1.png" alt="Students at Forum Information Academy" />
            </div>
        </div>
    );
};

export default Hero;
