import React from 'react';
import { Link } from 'react-router-dom';

const Hero = () => {
    return (
        <div className="hero-content">
            <div className="hero-text">
                <h1>Advance Your Career with Our Expert Courses</h1>
                <p>Learn from industry professionals and gain practical skills that will help you succeed in today's competitive job market.</p>
                <div className="hero-cta">
                    <Link to="/courses" className="btn btn-primary">Browse Courses</Link>
                    <Link to="/apply" className="btn btn-outline" style={{backgroundColor: 'transparent', color: 'white', borderColor: 'white'}}>Apply Now</Link>
                </div>
            </div>
            <div className="hero-image">
                <img src="/images/hero-image.jpg" alt="Students learning" />
            </div>
        </div>
    );
};

export default Hero;
